import React from 'react';
import HeaderProd from '../homepage/homepage';
import '../card/card.styles.css';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
// import '../prod-info/prod-info.styles.css';
import '../prod-info/prod-info.styles.css';


class ProdInfo extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      myitem: []
    }
    // console.log(this.props.items)
  }

  componentDidMount(){
    const item = this.props.items.filter(item => {
      console.log(item.name,this.props.match.url);
      if(`/${item.name}`===this.props.match.url){
          return item;
      }
    });
    this.setState({myitem: item})
  }
  render(){
    console.log(myitem);
    const { myitem } = this.state;
    if(myitem!==[]){
    return (
      <div className='container'>
        <div className='item-prod'>
          {/* <h1>{item[0].id}</h1> */}
          <h1> {myitem[0].name} </h1>
          <p>{myitem[0].description}</p>
          <h6>MRP: Rs.{myitem[0].price}</h6>
          <h6>OUR PRICE: Rs.{myitem[0].price}</h6>
        </div>

        <div className='item2-prod'>
          <img src='' alt='img'></img>
        </div>
        {/* <div className="container">
          <div className='item-prod'> */}
            {/* <HeaderProd/> */}

          {/* </div> */}

            {/* <h1> {item[0].id} </h1> */}
            {/* <p> {props.description} </p> */}
          {/* </div> */}
        {/* </div> */}
      </div>
    )}
    else{
      return null
    }
  }
}

const mapStateToProps = ({ itemlist: {items}}) => ({
  items
});


export default connect(mapStateToProps)(withRouter(ProdInfo));
