const INITIAL_STATE = {
    hidden: true
};

const categoriesReducer = (state = INITIAL_STATE, action) => {
    switch(action.type){
        case 'TOGGLE_CATEGORY_HIDDEN':
            return {
                ...state,
                hidden: !state.hidden
            }
        default:
            return state;
    }
}

export default categoriesReducer
