import React from 'react';
import { connect } from 'react-redux';

import { Card } from '../card/card.component';
import { CatCard } from '../cat-card/cat-card.component';
import { FeatCard } from '../feat-card/feat-card.component';
import { BrandCard } from '../brand-card/brand-card.component';
import { ItemCard } from '../item-card/item-card.component';

import './card-list.styles.css';

const CardList = props => (
  console.log(props),

  <div className='card-list'>
    {props.allitems.map(item => (
      <Card key={item.id} item={item}/>
    ))}

    {props.itemhidden ? null : props.items.map(prod => (
      <ItemCard key={prod.id} prod={prod}/>
    ))}

    {props.cathidden ? null : props.categories.map(cat => (
      <CatCard key={cat.id} cat={cat}/>
    ))}

    {props.feathidden ? null : props.features.map(feat => (
      <FeatCard key={feat.id} feat={feat}/>
    ))}

    {props.brandhidden ? null : props.brands.map(brand => (
      <BrandCard key={brand.id} brand={brand}/>
    ))}
  </div>
);

const mapStateToProps = ({category : { itemhidden,cathidden,brandhidden,feathidden }}) => ({
  cathidden,
  feathidden,
  brandhidden,
  itemhidden
});

export default connect(mapStateToProps)(CardList);
