import React, { Component } from 'react';

import { CardList } from './components/card-list/card-list.component';
import { SearchBox } from './components/search-box/search-box.component';

import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      items: [],
      brands: [],
      features: [],
      categories: [],
      searchField: '',
      checked: false
    };
  }

  componentDidMount() {
    fetch('https://my-json-server.typicode.com/banshilaldangi/ecommerce/products')
      .then(response => response.json())
      .then(users => {
        // console.log(users)
        this.setState({ items: users })
      });
    fetch('https://my-json-server.typicode.com/banshilaldangi/ecommerce/brands')
      .then(response => response.json())
      .then(users2 => {
        // console.log(users2);
        this.setState({ brands: users2})
      });
    fetch('https://my-json-server.typicode.com/banshilaldangi/ecommerce/features')
      .then(response => response.json())
      .then(users3 => {
        // console.log(users3);
        this.setState({ features: users3})
      });
  }

  onSearchChange = event => {
    this.setState({ searchField: event.target.value });
  };


  handleCheckboxChange = event => {
    fetch('https://my-json-server.typicode.com/banshilaldangi/ecommerce/categories')
      .then(response => response.json())
      .then(users4 => {
        // console.log(users4);
        this.setState({ categories: users4,
          checked: event.target.checked})
      });
  }


  render() {
    const { items, categories, searchField } = this.state;
    const filteredItems = items.filter(item =>
      item.name.toLowerCase().includes(searchField.toLowerCase())
    );

    return (
      <div className='App'>
        <h1>Items</h1>
        <input type='checkbox' checked={this.state.checked} onChange={this.handleCheckboxChange}/>
        <SearchBox onSearchChange={this.onSearchChange} />
        <CardList items={filteredItems} categories={categories}/>
      </div>
    );
  }
}

export default App;
