import React, { Component } from 'react';

import { CardList } from './components/card-list/card-list.component';
import { SearchBox } from './components/search-box/search-box.component';

import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      prime: {
        items: [],
        brands: [],
        features: [],
        categories: [],
      },
      searchField: ''
    };
    console.log(this.state);
  }

  componentDidMount() {
    fetch('https://my-json-server.typicode.com/banshilaldangi/ecommerce/products')
      .then(response => response.json())
      .then(users => this.setState({ items: users }));
    fetch('https://my-json-server.typicode.com/banshilaldangi/ecommerce/brands')
      .then(response => response.json())
      .then(users2 => this.setState({ brands: users2}));
    fetch('https://my-json-server.typicode.com/banshilaldangi/ecommerce/features')
      .then(response => response.json())
      .then(users3 => this.setState({ features: users3}));
    fetch('https://my-json-server.typicode.com/banshilaldangi/ecommerce/features')
      .then(response => response.json())
      .then(users4 => this.setState({ categories: users4}));
  }

  onSearchChange = event => {
    this.setState({ searchField: event.target.value });
  };

  render() {
    const { items, searchField } = this.state;
    const filteredItems = items.filter(item =>
      item.name.toLowerCase().includes(searchField.toLowerCase())
    );

    return (
      <div className='App'>
        <h1>Items</h1>
        <SearchBox onSearchChange={this.onSearchChange} />
        <CardList items={filteredItems} />
      </div>
    );
  }
}

export default App;
